import styled from 'styled-components';

export const StyledMenuBarContainer = styled.section`
  display: flex;
  flex-direction: column;
  gap: 8rem;
  background-color: #262730;
  padding: 3rem 0;
  /* min-width: 15%; */
  width: 30%;

  & .windows-icons-group {
    display: flex;
    gap: .4rem;
    padding-left: 2.5rem;
  }

  & .nav-items-container {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
  }

  & .nav-items-container .nav-item {
    display: flex;
    gap: 1.5rem;
    align-items: center;
    justify-content: center;
    padding: .4rem 1.5rem .4rem 1rem;
    border-right: 5px solid #fff;

  }

  & .nav-items-container .nav-item-active {
    border-right: 3px solid #fff;
  }

  & .nav-item .nav-item-text {
    font-size: 1.25rem;
  }
`

export const StyledWindowsIcon = styled.div<{bgcolor: string}>`
  width: 15px;
  height: 15px;
  background-color: ${({ bgcolor }) => bgcolor};
  border-radius: 50%;
`

export const StyledAllNotesContainer = styled.section`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 80%;

  & .top-container {
    display: flex;
    justify-content: space-between;
    padding: 4.5rem 4rem;
    align-items: center;
  }
`
